(1, 'Syarat Mendaftarkan Ormas', '<p style=\"text-align:justify\"><span style=\"font-size:12pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\"><span style=\"font-family:&quot;Bookman Old Style&quot;,serif\">Dasar : </span></span></span></p>\r\n\r\n<ol style=\"list-style-type:upper-alpha\">\r\n	<li style=\"text-align:justify\"><span style=\"font-size:12pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\"><span style=\"font-family:&quot;Bookman Old Style&quot;,serif\">Peraturan Pemerintah Republik Indonesia Nomor 58 Tahun 2016 tentang Pelaksanaan Undang-Undang Nomor 17 tahun 2013 tentang Organisasi Masyarakat;</span></span></span></li>\r\n</ol>\r\n\r\n<ol start=\"2\" style=\"list-style-type:upper-alpha\">\r\n	<li style=\"text-align:justify\"><span style=\"font-size:12pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\"><span style=\"font-family:&quot;Bookman Old Style&quot;,serif\">Peraturan Menteri Dalam Negeri Nomor 57 tahun 2017 tentang&nbsp;&nbsp; Pendaftaran dan Pengelolaan Sistem Informasi Ormas.</span></span></span></li>\r\n</ol>\r\n\r\n<p>&nbsp; &nbsp; &nbsp;S<span style=\"font-size:12pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\"><span style=\"font-family:&quot;Bookman Old Style&quot;,serif\">yarat-syarat Penerbitan STL Ormas</span></span></span></p>\r\n\r\n<ol>\r\n	<li style=\"text-align:justify\"><span style=\"font-size:12pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\"><span style=\"font-family:&quot;Bookman Old Style&quot;,serif\">Surat &nbsp;Permohonan</span></span></span></li>\r\n	<li style=\"text-align:justify\"><span style=\"font-size:12pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\"><span style=\"font-family:&quot;Bookman Old Style&quot;,serif\">SK Pengesahan Badan Hukum dari Kementerian Hukum dan HAM (AHU) bagi Ormas berbadan hukum atau;</span></span></span></li>\r\n</ol>\r\n\r\n<ol start=\"3\">\r\n	<li style=\"text-align:justify\"><span style=\"font-size:12pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\"><span style=\"font-family:&quot;Bookman Old Style&quot;,serif\">Surat Keterangan Terdaftar (SKT) yang diterbitkan Kementerian Dalam Negeri, bagi Ormas yang tidak berbadan hukum;</span></span></span></li>\r\n	<li style=\"text-align:justify\"><span style=\"font-size:12pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\"><span style=\"font-family:&quot;Bookman Old Style&quot;,serif\">Akta Notaris;</span></span></span></li>\r\n</ol>\r\n\r\n<ol start=\"5\">\r\n	<li style=\"text-align:justify\"><span style=\"font-size:12pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\"><span style=\"font-family:&quot;Bookman Old Style&quot;,serif\">Anggaran Dasar dan Anggaran Rumah Tangga&nbsp; (AD/ART);</span></span></span></li>\r\n</ol>\r\n\r\n<ol start=\"6\">\r\n	<li style=\"text-align:justify\"><span style=\"font-size:12pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\"><span style=\"font-family:&quot;Bookman Old Style&quot;,serif\">Program Kerja Organisasi;</span></span></span></li>\r\n</ol>\r\n\r\n<p style=\"margin-left:57px; text-align:justify\">&nbsp;</p>\r\n\r\n<ol start=\"7\">\r\n	<li style=\"text-align:justify\"><span style=\"font-size:12pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\"><span style=\"font-family:&quot;Bookman Old Style&quot;,serif\">SK Pengurus Ormas dari DPP/DPD atau sebutan lainnya, berdasarkan&nbsp; AHU atau SKT dari Kementerian.</span></span></span></li>\r\n</ol>\r\n\r\n<ol start=\"8\">\r\n	<li style=\"text-align:justify\"><span style=\"font-size:12pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\"><span style=\"font-family:&quot;Bookman Old Style&quot;,serif\">Daftar riwayat hidup, pas photo 4x6 1 lbr, dan copy KTP masing-masing Ketua, Sekretaris dan Bendahara.</span></span></span></li>\r\n</ol>\r\n\r\n<ol start=\"9\">\r\n	<li style=\"text-align:justify\"><span style=\"font-size:12pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\"><span style=\"font-family:&quot;Bookman Old Style&quot;,serif\">Surat Keterangan Domisili kantor sekretariat ormas dari Kelurahan dengan lampiran :</span></span></span>\r\n\r\n	<ul>\r\n		<li style=\"text-align:justify\"><span style=\"font-size:12pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\"><span style=\"font-family:&quot;Bookman Old Style&quot;,serif\">Surat bukti kontrak, sewa atau milik sendiri kantor sekretariat.</span></span></span></li>\r\n		<li style=\"text-align:justify\"><span style=\"font-size:12pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\"><span style=\"font-family:&quot;Bookman Old Style&quot;,serif\">Photo kantor sekretariat tampak depan. </span></span></span></li>\r\n	</ul>\r\n	</li>\r\n</ol>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:12pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\"><span style=\"font-family:&quot;Bookman Old Style&quot;,serif\">&nbsp; &nbsp; &nbsp; &nbsp; 10. NPWP Organisasi.</span></span></span></p>\r\n\r\n<p style=\"margin-left:57px; text-align:justify\"><span style=\"font-size:12pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\"><span style=\"font-family:&quot;Bookman Old Style&quot;,serif\">11. Mengisi formulir data organisasi (form 1) dan Surat Pernyataan (form 2) </span></span></span></p>\r\n\r\n<p style=\"margin-left:57px; text-align:justify\">&nbsp;</p>\r\n\r\n<p style=\"margin-left:24px; text-align:justify\"><span style=\"font-size:12pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\"><span style=\"font-family:&quot;Bookman Old Style&quot;,serif\">12. Map Biola : </span></span></span></p>\r\n\r\n<ul>\r\n	<li style=\"list-style-type:none\">\r\n	<ul>\r\n		<li style=\"text-align:justify\"><span style=\"font-size:12pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\"><span style=\"font-family:&quot;Bookman Old Style&quot;,serif\">Warna Merah untuk Ormas berbasis Sosial kontrol dan Hukum.</span></span></span></li>\r\n		<li style=\"text-align:justify\"><span style=\"font-size:12pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\"><span style=\"font-family:&quot;Bookman Old Style&quot;,serif\">Warna Kuning untuk Ormas berbasis Sosial Kemasyarakatan dan lingkungan hidup.</span></span></span></li>\r\n	</ul>\r\n	</li>\r\n	<li style=\"text-align:justify\"><span style=\"font-size:12pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\"><span style=\"font-family:&quot;Bookman Old Style&quot;,serif\">Warna Hijau untuk Ormas berbasis Sosial Keagamaan.</span></span></span></li>\r\n	<li style=\"text-align:justify\"><span style=\"font-size:12pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\"><span style=\"font-family:&quot;Bookman Old Style&quot;,serif\">Warna Biru untuk Ormas Berbasis Kepemudaan</span></span></span></li>\r\n</ul>\r\n\r\n<p style=\"margin-left:85px; text-align:justify\">&nbsp;</p>\r\n'),
(2, 'Regulasi Ormas ', '<p>1. UU NO 16 Tahun 2017 tentang Perppu Ormas menjadi UU</p>\r\n\r\n<p>2. Peraturan Menteri Dalam Negeri Nomor 57 tahun 2017 tentang &nbsp; Pendaftaran dan Pengelolaan Sistem Informasi Ormas.</p>\r\n'),
