INSERT INTO `halaman` (`id`, `judul`, `konten`, `gambar`) VALUES
(1, 'Tentang Kami', '<p>Tentang Kami</p>\r\n', '067fbeac7caf3258a665bcda3cf84a28.jpg'),
(2, 'Pemahaman Radikalisme', '<div class=\"WordSection1\">\r\n<p style=\"text-align:justify\">Regulasi Undang-undang tentang Radikal</p>\r\n\r\n<p style=\"text-align:justify\">UNDANG-UNDANG REPUBLIK INDONESIA<br />\r\nNOMOR 5 TAHUN 2018<br />\r\nTENTANG<br />\r\nPERUBAHAN ATAS UNDANG-UNDANG NOMOR 15 TAHUN 2003<br />\r\nTENTANG PENETAPAN PERATURAN PEMERINTAH PENGGANTI</p>\r\n\r\n<p style=\"text-align:justify\">UNDANG-UNDANG NOMOR 1 TAHUN 2002 TENTANG PEMBERANTASAN<br />\r\nTINDAK PIDANA TERORISME MENJADI UNDANG-UNDANG</p>\r\n\r\n<p style=\"text-align:justify\">&nbsp;</p>\r\n\r\n<p style=\"text-align:justify\">PERATURAN PEMERINTAH REPUBLIK INDONESIA</p>\r\n\r\n<p style=\"text-align:justify\">NOMOR 77 TAHUN 2019</p>\r\n\r\n<p style=\"text-align:justify\">PENCEGAHAN TINDAK PIDANA TERORISME DAN&nbsp;PELINDUNGAN TERHADAP PET\\TYIDIK, PENUNTUT UMUM, HAKIM, DAN PETU GAS PEMASYARAKATAN</p>\r\n\r\n<p style=\"text-align:justify\">&nbsp;</p>\r\n\r\n<p style=\"text-align:justify\">&nbsp;</p>\r\n\r\n<p style=\"text-align:justify\"><strong><span style=\"font-size:11pt\"><span style=\"font-size:12.0pt\">Radikal</span></span></strong></p>\r\n\r\n<p style=\"margin-left:38px\">&nbsp;</p>\r\n\r\n<p style=\"margin-left:47px; text-align:justify\"><span style=\"font-size:12pt\">Kamus Besar Bahasa Indonesia menyebutkan radikal dalam arti &ldquo;secara mendasar (sampai kepada hal yang perinsip) ; atau maju dalam berpikir atau bertindak&rdquo;. Dalam konteks ini, berfikir radikal merupakan proses berfikir secara mendalam sampai pada makna kebenaran yang tertinggi. Melalui proses berfikir secara radikal, manusia mampu memperoleh kebenaran dan menemukan sejumlah penemuan ilmiah (ilmu pengetahuan). Berfikir secara radikal dapat pula dimaknai sebagai upaya berfikir sampai pada akar persoalan yang ada, sehingga diharapkan sebuah keputusan benar-benar bijak dan tidak salah dalam mengambil keputusan.</span></p>\r\n\r\n<p style=\"margin-left:47px; text-align:justify\"><span style=\"font-size:12pt\">Berbeda dengan istilah &ldquo;radikalisme&rdquo;. Dalam Kamus Besar Bahasa Indonesia disebutkan bahwa &ldquo;radikalisme merupakan paham atau aliran yang menginginkan perubahan atau pembaharuan sosial dan politik dengan cara kekerasan (sikap ekstrem) atau drastis&rdquo;. Demikian pula dalam berbagai kamus ditemukan bahwa makna radikalisme merupakan aksi mencolok untuk menyerukan paham ekstrem agar diikuti oleh banyak orang.</span></p>\r\n\r\n<p style=\"margin-left:47px; text-align:justify\"><span style=\"font-size:12pt\">Bila dirujuk dari makna di atas, terlihat bahwa berfikir radikal sangat diperlukan dalam pengembangan ilmu pengetahuan dan menampilkan sikap bijaksana sebelum mengambil sebuah keputusan. Namun, bertolak belakang dengan makna radikalisme. Sebab, radikalisme merupakan paham ekstrem yang acapkali menggunakan pemaksaan dan kekerasan agar apa yang diinginkan (diyakini) diterima oleh orang lain dengan menghalalkan segala cara. Kelompok penganut radikalisme ini bukan hanya berhadapan pada negara (politik), akan tetapi juga terkadang berhadapan dengan antar agama, suku, budaya, bahkan intern agama. Gerakan radikalisme biasanya bersifat masif dan membentuk karakter intoleran dan memandang kebenaran tunggal hanya milik kelompoknya. Melihat kondisi ini, gerakan radikalisme sangat berbahaya bagi tatanan suatu negara dan peradaban umat manusia. Eksistensi radikalisme menjadikannya sebagai musuh bersama dan perlu ditindak secara serius.</span></p>\r\n\r\n<p style=\"margin-left:47px; text-align:justify\">&nbsp;</p>\r\n\r\n<p style=\"margin-left:38px; text-align:justify\"><span style=\"font-size:12pt\">Pengertian ekstrimitas ialah seseorang atau sekelompok orang yang berpendirian keras,fanatik, berpandangan sempit terhadap suatu ide, ideologi atau pandangan yang dimilikinya.Sikap ektrim bisa timbul juga karena rasa kesukuan, golongan social atau karena frustasi.Ekstrim kiri merupakan bahaya laten. Berdasarkan praktek-prakteknya menunjukkan bahwa ekstrim kiri itu memang membahayakan bagi keutuhan negara kita. Kita harus tetap waspada terhadap golongan ekstrim kiri. Kita sebagai generasi muda hendaknya mengamalkan nilai-nilai Pancasila dalam kehidupan berbangsa dan bernegara, sebagai suatu bentuk pertahanan diri. Ekstrim kanan mengadakan penyimpangan dari pola dasar haluan masyarakat atau negara yang telah disepakati secara resmi oleh masyarakat atau negara yang bersangkutan, karena pola dasar (misalnya haluan negara)mereka anggap tidak sanggung menjamin tercapainya nilai spiritual keagamaan yang mereka inginkan.</span></p>\r\n\r\n<p style=\"margin-left:38px; text-align:justify\">&nbsp;</p>\r\n\r\n<p style=\"margin-left:47px; text-align:justify\"><span style=\"font-size:12pt\">Kamus Besar Bahasa Indonesia menyebutkan radikal dalam arti &ldquo;secara mendasar (sampai kepada hal yang perinsip) ; atau maju dalam berpikir atau bertindak&rdquo;. Dalam konteks ini, berfikir radikal merupakan proses berfikir secara mendalam sampai pada makna kebenaran yang tertinggi. Melalui proses berfikir secara radikal, manusia mampu memperoleh kebenaran dan menemukan sejumlah penemuan ilmiah (ilmu pengetahuan). Berfikir secara radikal dapat pula dimaknai sebagai upaya berfikir sampai pada akar persoalan yang ada, sehingga diharapkan sebuah keputusan benar-benar bijak dan tidak salah dalam mengambil keputusan.</span></p>\r\n\r\n<p style=\"margin-left:47px; text-align:justify\"><span style=\"font-size:12pt\">Berbeda dengan istilah &ldquo;radikalisme&rdquo;. Dalam Kamus Besar Bahasa Indonesia disebutkan bahwa &ldquo;radikalisme merupakan paham atau aliran yang menginginkan perubahan atau pembaharuan sosial dan politik dengan cara kekerasan (sikap ekstrem) atau drastis&rdquo;. Demikian pula dalam berbagai kamus ditemukan bahwa makna radikalisme merupakan aksi mencolok untuk menyerukan paham ekstrem agar diikuti oleh banyak orang.</span></p>\r\n\r\n<p style=\"margin-left:47px; text-align:justify\"><span style=\"font-size:12pt\">Bila dirujuk dari makna di atas, terlihat bahwa berfikir radikal sangat diperlukan dalam pengembangan ilmu pengetahuan dan menampilkan sikap bijaksana sebelum mengambil sebuah keputusan. Namun, bertolak belakang dengan makna radikalisme. Sebab, radikalisme merupakan paham ekstrem yang acapkali menggunakan pemaksaan dan kekerasan agar apa yang diinginkan (diyakini) diterima oleh orang lain dengan menghalalkan segala cara. Kelompok penganut radikalisme ini bukan hanya berhadapan pada negara (politik), akan tetapi juga terkadang berhadapan dengan antar agama, suku, budaya, bahkan intern agama. Gerakan radikalisme biasanya bersifat masif dan membentuk karakter intoleran dan memandang kebenaran tunggal hanya milik kelompoknya. Melihat kondisi ini, gerakan radikalisme sangat berbahaya bagi tatanan suatu negara dan peradaban umat manusia. Eksistensi radikalisme menjadikannya sebagai musuh bersama dan perlu ditindak secara serius.</span></p>\r\n\r\n<p style=\"margin-left:47px; text-align:justify\">&nbsp;</p>\r\n\r\n<p style=\"margin-left:38px; text-align:justify\"><span style=\"font-size:12pt\">Pengertian ekstrimitas ialah seseorang atau sekelompok orang yang berpendirian keras,fanatik, berpandangan sempit terhadap suatu ide, ideologi atau pandangan yang dimilikinya.Sikap ektrim bisa timbul juga karena rasa kesukuan, golongan social atau karena frustasi.Ekstrim kiri merupakan bahaya laten. Berdasarkan praktek-prakteknya menunjukkan bahwa ekstrim kiri itu memang membahayakan bagi keutuhan negara kita. Kita harus tetap waspada terhadap golongan ekstrim kiri. Kita sebagai generasi muda hendaknya mengamalkan nilai-nilai Pancasila dalam kehidupan berbangsa dan bernegara, sebagai suatu bentuk pertahanan diri. Ekstrim kanan mengadakan penyimpangan dari pola dasar haluan masyarakat atau negara yang telah disepakati secara resmi oleh masyarakat atau negara yang bersangkutan, karena pola dasar (misalnya haluan negara)mereka anggap tidak sanggung menjamin tercapainya nilai spiritual keagamaan yang mereka inginkan.</span></p>\r\n\r\n<p style=\"margin-left:38px\">&nbsp;</p>\r\n\r\n<ol>\r\n	<li><span style=\"font-size:12pt\"><span style=\"font-family:Arial,sans-serif\">ISU STRATEGIS</span></span></li>\r\n</ol>\r\n\r\n<h2 style=\"margin-left:63px\"><span style=\"font-size:12pt\"><span style=\"font-family:Arial,sans-serif\">KONDISI SAAT INI (Das Sein)</span></span></h2>\r\n\r\n<ol>\r\n	<li style=\"list-style-type:none\">\r\n	<ol>\r\n		<li style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-size:12.0pt\">Kurangnya pengetahuan Masyarakat terhadap upaya pencegahan dini menghadapi masalah.</span></span></li>\r\n		<li style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-size:12.0pt\"><span style=\"color:#111111\">Minimnya informasi yang diperoleh dari masyarakat untuk upaya pencegahan dini.</span></span></span></li>\r\n		<li style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-size:12.0pt\"><span style=\"color:#212121\">Belum adanya Sumber Daya Manusia yang mengelola informasi mengenai </span></span><span style=\"font-size:12.0pt\"><span style=\"color:#111111\">pencegahan dini dan radikal</span></span></span></li>\r\n		<li style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-size:12.0pt\"><span style=\"color:#212121\">Belum adanya singkronisasi data dan aplikasi yang bisa diakses masyarakat dalam menyampaikan informasi kepada pemerintah.</span></span></span></li>\r\n		<li style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-size:12.0pt\"><span style=\"color:#111111\">Belum optimalnya pencegahan dini/kewaspadaan dini terhadap permasalahan yang muncul di masyarakat terutama bidang ideologi, politik, ekonomi, sosial dan budaya</span></span></span></li>\r\n	</ol>\r\n	</li>\r\n</ol>\r\n\r\n<p style=\"margin-left:77px\"><span style=\"font-size:12pt\"><span style=\"font-family:Arial,sans-serif\">KONDISI YANG DIHARAPKAN (Das Solen)</span></span></p>\r\n\r\n<ol>\r\n	<li style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-size:12.0pt\">Terwujudnya pengetahuan Masyarakat terhadap upaya pencegahan dini menghadapi masalah</span></span></li>\r\n	<li style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-size:12.0pt\"><span style=\"color:#111111\">Meningkatnya informasi yang diperoleh dari masyarakat untuk upaya pencegahan dini</span></span></span></li>\r\n	<li style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-size:12.0pt\"><span style=\"color:#212121\">Tersedianya Sumber Daya Manusia yang mengelola informasi mengenai </span></span><span style=\"font-size:12.0pt\"><span style=\"color:#111111\">pencegahan dini dan radikal</span></span></span></li>\r\n	<li style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-size:12.0pt\"><span style=\"color:#212121\">Terwujudnya singkronisasi data dan aplikasi yang bisa diakses masyarakat dalam menyampaikan informasi kepada pemerintah.</span></span></span></li>\r\n	<li style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-size:12.0pt\"><span style=\"color:#111111\">Meningkatnya upaya pencegahan dini/kewaspadaan dini terhadap permasalahan yang muncul di masyarakat terutama bidang ideologi, politik, ekonomi, sosial dan budaya.&nbsp;</span></span></span></li>\r\n</ol>\r\n</div>\r\n', '1bde833f8d0625957a3284a70a25e7eb.png'),
(3, 'Ponpes tolak Radikal di Kota  Lubuklinggau', '<p>Adanya surat kaleng yang menuduh salah satu Pondok Pesantren (Ponpes) di Kota Lubuklinggau yang didalamnya diduga terdapat organisasi ISIS dan berbuat radikal, Persatuan Ponpes Kota Lubuklinggau sepakat menolak dan menentang apapun bentuk organisasi terlarang ISIS.</p>\r\n\r\n<p>Pernyataan tersebut disampaikan perwakilan Persatuan Ponpes Kota Lubuklinggau, Ustadz H. A Fahmi saat memberikan keterangan pers dihadapan wartawan usai berdialog dengan Kapolres Lubuklinggau, AKBP Sunandar didampingi Wakapolres, Kompol Andi Kumara di Mapolres, Jum&#39;at (8/12/2017).</p>\r\n\r\n<p>&quot;Kami mengklarifikasi tentang adanya surat kaleng yang menuduh salah satu Ponpes di Kota Lubuklinggau didalamnya diduga terdapat organisasi ISIS dan berbuat radikal. Itu sama sekali tidak benar, oleh sebab itu kami merasa perlu untuk menjelaskan ini,&quot; ucap dia.&nbsp;</p>\r\n\r\n<p>Bahkan, menurutnya selama ini seluruh Ponpes di Kota Lubuklinggau bahu membahu bersama Polres dan pemerintah setempat mengawasi dan mengantisipasi apapun yang berbau teroris serta radikalisme.</p>\r\n\r\n<p>&quot;Sekali lagi kami tegaskan, bahwa itu sama sekali tidak benar. Kami bersama dan bekerjasama dengan polisi mengawasi Kota Lubuklinggau,&quot; katanya.</p>\r\n\r\n<p>Sementara itu, Kapolres Kota Lubuklinggau, AKBP Sunandar mengungkapkan, sempat menyebar di Medsos tentang adanya surat kaleng berisi dugaan tentang ISIS. Oleh sebab itu, kepolisian mengundang ulama dan pihak Ponpes untuk bersama-sama mengklarifikasi tuduhan tersebut.</p>\r\n\r\n<p>&quot;Itu ada sedikit letupan-letupan dalam nuansa berkehidupan masyarakat, kami dari kepolisian memanggil seluruhnya untuk mengklarifikasi. Dan kami yakin serta percaya Ponpes di Linggau tidak ada ISIS,&quot; pungkasnya.</p>\r\n', '6f1c92e5543ebb93eb85f0fee649a123.png'),
(4, 'Kewaspadaan Dini', '<p style=\"text-align:justify\"><img alt=\"\" src=\"https://kesbangpol.lubuklinggaukota.go.id/assets//album/25.jpg\" style=\"height:444px; width:986px\" /></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:18px\">Kewaspadaan Dini adalah serangkaian upaya/tindakan untuk menangkal segala potensi ancaman, tantangan, hambatan dan gangguan dengan meningkatkan pendeteksian dan pencegahan dini.</span></p>\r\n\r\n<ol>\r\n	<li style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-size:12.0pt\">Tim Kewaspadaan Dini Daerah yang selanjutnya disingkat sebagai TKDD, dibentuk berdasarkan:</span></span>\r\n\r\n	<ol style=\"list-style-type:lower-alpha\">\r\n		<li style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-size:12.0pt\">Peraturan Menteri Dalam Negeri Nomor 46 Tahun 2019 Tentang Perubahan Atas Peraturan Menteri Dalam Negeri Nomor 2 Tahun 2018 Tentang Kewaspadaan Dini Di Daerah yang tertuang dalam pasal 25 bahwa Tim Kewaspadaan Dini Pemerintah Daerah harus dibentuk paling lama 1 (satu) tahun sejakPeraturan Menteri ini diundangkan.</span></span></li>\r\n		<li style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-size:12.0pt\">Keputusan&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Wali&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Kota&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Lubuklinggau</span> <span style=\"font-size:12.0pt\">Nomor 129/KPTS/BAKESBANGPOL/2021 tentang Penetapan Tim Kewaspadaan Dini Pemerintah Daerah Kota Lubuklinggau 2021 yang memiliki tugas sebagai berikut:</span></span>\r\n		<ol>\r\n			<li style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-size:12.0pt\">merencanakan, melaksanakan dan merumuskan kegiatan Kewaspadaan Dini Pemerintah Daerah di daerah Kota Lubuklinggau;</span></span></li>\r\n			<li style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-size:12.0pt\">mencari, mengumpulkan, mengoordinasikan dan mengomunikasikan data serta informasi/bahan keterangan dengan instansi vertikal mengenai potensi, gejala, atau peristiwa timbulnya ATHG di daerah Kota Lubuklinggau;</span></span></li>\r\n			<li style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-size:12.0pt\">mengkoordinasikan fungsi, kegiatan Tim Kewaspadaan Dini Pemerintah Daerah di daerah Kecamatan di wilayahnya dan FKDM di Kota Lubuklinggau dalam pelaksanaan Kewaspadaan Dini terhadap potensi, gejala, atau peristiwa timbulnya ATHG di daerah Kota Lubuklinggau yang mengancam stabilitas nasional; dan</span></span></li>\r\n			<li style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-size:12.0pt\">memberikan rekomendasi kepada Wali Kota sebagai bahan kebijakan yang berkaitan dengan Pendeteksian Dini dan Pencegahan Dini terhadap ATHG di daerah Kota Lubuklinggau.</span></span></li>\r\n		</ol>\r\n		</li>\r\n	</ol>\r\n	</li>\r\n</ol>\r\n\r\n<p style=\"margin-left:47px; margin-right:3px; text-align:justify\">&nbsp;</p>\r\n\r\n<p style=\"margin-left:47px; margin-right:3px; text-align:justify\"><span style=\"font-size:12pt\">Adanya <a href=\"https://www.bola.com/tag/integrasi-nasional\"><strong>integrasi nasional </strong></a>sangat penting bagi Indonesia agar tercipta keselarasan bangsa di tengah-tengah keadaan masyarakat yang berbeda-beda. Sementara, menurut Kamus Besar Bangsa Indonesia (KBBI), integrasi adalah pembauran sampai menjadi satu kesatuan yang bulat dan utuh. Adapun kata &#39;nasional&#39; berarti bangsa. Selain itu, integrasi nasional sangat diperlukan demi keutuhan negara dari berbagai ancaman. Ancaman tersebut&nbsp; &nbsp;juga&nbsp;&nbsp; &nbsp;berpotensi&nbsp;&nbsp; &nbsp;merusak&nbsp;&nbsp; &nbsp;integrasi&nbsp;&nbsp; &nbsp;nasional.&nbsp;&nbsp; &nbsp;Secara umum, ancaman terhadap integrasi nasional dibagi menjadi dua, yakni ancaman militer dan nonmiliter. Berikut ini rangkuman tentang macam- macam ancaman terhadap <a href=\"https://www.bola.com/tag/integrasi-nasional\"><strong>integrasi nasional</strong></a><strong>.</strong></span></p>\r\n\r\n<p style=\"margin-right:3px\">&nbsp;</p>\r\n\r\n<h2 style=\"margin-right:3px; text-align:justify\"><span style=\"font-size:12pt\"><span style=\"font-family:Arial,sans-serif\">Ancaman Militer</span></span></h2>\r\n\r\n<p style=\"margin-left:47px; margin-right:3px; text-align:justify\"><span style=\"font-size:12pt\">Ancaman militer adalah ancaman menggunakan kekuatan bersenjata yang terorganisasi dan mempunyai kemampuan yang bisa membahayakan kedaulatan negara, keutuhan wilayah negara, dan keselamatan segenap bangsa. Ancaman militer ini dibagi menjadi dua, yaitu ancaman dalam negeri dan luar negeri.</span></p>\r\n\r\n<ol>\r\n	<li style=\"list-style-type:none\">\r\n	<ol>\r\n		<li style=\"list-style-type:none\">\r\n		<ol>\r\n			<li style=\"list-style-type:none\">\r\n			<ol>\r\n				<li><span style=\"font-size:12pt\"><span style=\"font-family:Arial,sans-serif\">Ancaman Militer Dalam Negeri</span></span></li>\r\n			</ol>\r\n			</li>\r\n		</ol>\r\n		</li>\r\n	</ol>\r\n	</li>\r\n</ol>\r\n\r\n<p style=\"margin-left:47px; margin-right:3px; text-align:justify\"><span style=\"font-size:12pt\">Ancaman militer dalam negeri adalah bentuk ancaman yang datangnya bersumber dari pihak internal atau dari dalam negeri. Bentuk ancaman ini harus diwaspadai karena bisa muncul kapan saja tanpa ada tanda-tanda, bisa dalam skala kecil seperti konflik masyarakat biasa, atau dalam skala besar. Contoh ancaman militer dalam negeri:</span></p>\r\n\r\n<ul>\r\n	<li style=\"list-style-type:none\">\r\n	<ul>\r\n		<li style=\"list-style-type:none\">\r\n		<ul>\r\n			<li style=\"list-style-type:none\">\r\n			<ul>\r\n				<li style=\"list-style-type:none\">\r\n				<ul>\r\n					<li style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-size:12.0pt\">Disintegrasi bangsa, melalui macam-macam gerakan separatis beradasarkan sebuah sentimen kesukuan atau pemberontakan akibat ketidakpuasan daerah terhadap kebijakan pemerintahan pusat.</span></span></li>\r\n					<li style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-size:12.0pt\">Keresahan sosial akibat ketimpangan kebijakan ekonomi dan pelanggaran hak asasi manusia yang pada gilirannya dapat mengakibatkan suatu kerusuhan masal.</span></span></li>\r\n					<li style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-size:12.0pt\">Upaya penggantian ideologi pancasila dengan ideologi yang lain yang ekstrem atau tidak sesuai kebiasan dari masyarakat Indonesia.</span></span></li>\r\n					<li style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-size:12.0pt\">Makar dan penggulingan pemerintahan yang sah dan konstitusional.</span></span></li>\r\n				</ul>\r\n				</li>\r\n			</ul>\r\n			</li>\r\n		</ul>\r\n		</li>\r\n	</ul>\r\n	</li>\r\n</ul>\r\n\r\n<p style=\"margin-left:76px; margin-right:3px; text-align:justify\">&nbsp;</p>\r\n\r\n<ol>\r\n	<li style=\"list-style-type:none\">\r\n	<ol>\r\n		<li style=\"list-style-type:none\">\r\n		<ol>\r\n			<li style=\"list-style-type:none\">\r\n			<ol start=\"2\">\r\n				<li style=\"text-align:justify\"><span style=\"font-size:12pt\"><span style=\"font-family:Arial,sans-serif\">Ancaman Militer Luar Negeri</span></span></li>\r\n			</ol>\r\n			</li>\r\n		</ol>\r\n		</li>\r\n	</ol>\r\n	</li>\r\n</ol>\r\n\r\n<p style=\"margin-left:47px; margin-right:3px; text-align:justify\"><span style=\"font-size:12pt\">Ancaman militer luar negeri adalah bentuk ancaman yang datangnya bersumber dari pihak eksternal atau dari luar negeri. Contoh ancaman militer luar negeri:</span></p>\r\n\r\n<ul>\r\n	<li style=\"list-style-type:none\">\r\n	<ul>\r\n		<li style=\"list-style-type:none\">\r\n		<ul>\r\n			<li style=\"list-style-type:none\">\r\n			<ul>\r\n				<li style=\"list-style-type:none\">\r\n				<ul>\r\n					<li style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-size:12.0pt\">Pelanggaran batas negara yang dilakukan oleh negara lain.</span></span></li>\r\n					<li style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-size:12.0pt\">Pemberontakan senjata yang dilakukan oleh negara lain.</span></span></li>\r\n					<li style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-size:12.0pt\">Aksi teror yang dilakukan oleh terorisme internasional.</span></span></li>\r\n				</ul>\r\n				</li>\r\n			</ul>\r\n			</li>\r\n		</ul>\r\n		</li>\r\n	</ul>\r\n	</li>\r\n</ul>\r\n\r\n<p style=\"margin-left:47px; margin-right:3px\">&nbsp;</p>\r\n\r\n<p style=\"margin-right:3px\">&nbsp;</p>\r\n\r\n<h2 style=\"margin-right:3px\"><span style=\"font-size:12pt\"><span style=\"font-family:Arial,sans-serif\">Bentuk-Bentuk Ancaman Militer</span></span></h2>\r\n\r\n<p style=\"margin-right:3px\"><span style=\"font-size:12pt\">Bentuk-bentuk Ancaman Militer sebagai berikut:</span></p>\r\n\r\n<ol>\r\n	<li style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-size:12.0pt\">Agresi</span></span></li>\r\n</ol>\r\n\r\n<p style=\"margin-left:76px; margin-right:3px; text-align:justify\"><span style=\"font-size:12pt\">Agresi adalah ancaman militer yang menggunakan kekuatan bersenjata oleh negara lain terhadap suatu negara. Hal tersebut dapat membahayakan kedaulatan, keutuhan wilayah negara, dan keselamatan segenap bangsa tersebut.</span></p>\r\n\r\n<ol start=\"2\">\r\n	<li style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-size:12.0pt\">Invasi</span></span></li>\r\n</ol>\r\n\r\n<p style=\"margin-left:76px; margin-right:3px; text-align:justify\"><span style=\"font-size:12pt\">Invasi yaitu suatu serangan yang dilakukan oleh kekuatan bersenjata dari negara lain terhadap wilayah NKRI.</span></p>\r\n\r\n<ol start=\"3\">\r\n	<li style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-size:12.0pt\">Bombardemen</span></span></li>\r\n</ol>\r\n\r\n<p style=\"margin-left:76px; margin-right:3px; text-align:justify\"><span style=\"font-size:12pt\">Bombardemen adalah suatu penggunaan senjata yang dilakukan oleh angkatan bersenjata dari negara lain terhadap NKRI.</span></p>\r\n\r\n<ol start=\"4\">\r\n	<li style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-size:12.0pt\">Blokade</span></span></li>\r\n</ol>\r\n\r\n<p style=\"margin-left:76px; margin-right:3px; text-align:justify\"><span style=\"font-size:12pt\">Blokade merupakan kegiatan penghambatan yang biasanya dilakukan di daerah pelabuhan atau pantai atau wilayah udara NKRI oleh angkatan bersenjata negara lain.</span></p>\r\n\r\n<ol start=\"5\">\r\n	<li style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-size:12.0pt\">Spionase</span></span></li>\r\n</ol>\r\n\r\n<p style=\"margin-left:76px; margin-right:3px; text-align:justify\"><span style=\"font-size:12pt\">Spionase adalah ancaman militer yang dilakukan oleh negara lain terhadap suatu negara yang kegiatannya berupa mata-mata. Hal tersebut bertujuan untuk mencari dan mendapatkan dokumen rahasia militer suatu negara.</span></p>\r\n\r\n<p style=\"margin-left:76px; margin-right:3px; text-align:justify\">&nbsp;</p>\r\n\r\n<p style=\"margin-left:76px; margin-right:3px; text-align:justify\">&nbsp;</p>\r\n\r\n<ol start=\"6\">\r\n	<li style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-size:12.0pt\">Sabotase</span></span></li>\r\n</ol>\r\n\r\n<p style=\"margin-left:85px; margin-right:3px; text-align:justify\"><span style=\"font-size:12pt\">Sabotase adalah ancaman militer yang dilakukan oleh suatu negara dengan tujuan untuk merusak instalasi militer dan objek vital nasional. Tentunya sabotase ini dapat membahayakan keselamatan suatu bangsa.</span></p>\r\n\r\n<ol start=\"7\">\r\n	<li style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-size:12.0pt\">Perang saudara</span></span></li>\r\n</ol>\r\n\r\n<p style=\"margin-left:85px; margin-right:3px; text-align:justify\"><span style=\"font-size:12pt\">Terjadinya perang saudara yang menggunakan senjata juga termasuk ancaman militer.</span></p>\r\n\r\n<h2 style=\"margin-right:3px\">&nbsp;</h2>\r\n\r\n<h2 style=\"margin-right:3px\"><span style=\"font-size:12pt\"><span style=\"font-family:Arial,sans-serif\">Ancaman Nonmiliter</span></span></h2>\r\n\r\n<p style=\"margin-left:47px; margin-right:3px; text-align:justify\"><span style=\"font-size:12pt\">Ancaman nonmiliter atau nirmiliter memiliki karakteristik yang berbeda dengan ancaman militer, yaitu tidak bersifat fisik serta bentuknya tidak terlihat seperti ancaman militer. Ancaman nonmiliter bisa berbentuk ancaman terhadap ideologi, politik, ekonomi, sosial budaya, pertahanan, dan keamanan.</span></p>\r\n\r\n<h2 style=\"margin-right:3px\">&nbsp;</h2>\r\n\r\n<h2 style=\"margin-right:3px\"><span style=\"font-size:12pt\"><span style=\"font-family:Arial,sans-serif\">Bentuk-Bentuk Ancaman Nonmiliter</span></span></h2>\r\n\r\n<ol>\r\n	<li style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-size:12.0pt\">Ancaman Berdimensi Ideologi</span></span></li>\r\n</ol>\r\n\r\n<p style=\"margin-left:47px; margin-right:3px; text-align:justify\"><span style=\"font-size:12pt\">Ancaman tersebut pernah terjadi pada Uni Soviet yang mengalami perubahan dari ideologi komunis menjadi liberal. Sistem politik internasional mengalami perubahan semenjak Uni Soviet runtuh sehingga paham komunis tidak populer lagi.</span></p>\r\n\r\n<ol start=\"2\">\r\n	<li style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-size:12.0pt\">Ancaman Berdimensi Politik</span></span></li>\r\n</ol>\r\n\r\n<p style=\"margin-left:47px; margin-right:3px; text-align:justify\"><span style=\"font-size:12pt\">Politik merupakan instrumen utama dalam menggerakkan perang. Hal ini membuktikan ancaman politik bisa menumbangkan suatu rezim pemerintahan, bahkan juga bisa menghancurkan suatu negara.</span></p>\r\n\r\n<ol start=\"3\">\r\n	<li style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-size:12.0pt\">Ancaman Berdimensi Ekonomi</span></span></li>\r\n</ol>\r\n\r\n<p style=\"margin-left:47px; margin-right:3px; text-align:justify\"><span style=\"font-size:12pt\">Ekonomi merupakan satu di antara penentu posisi tawar dari setiap negara dalam pergaulan internasional. Kondisi ekonomi tentu sangat menentukan dalam pertahanan negara.</span></p>\r\n\r\n<h2 style=\"margin-right:3px\">&nbsp;</h2>\r\n\r\n<h2 style=\"margin-right:3px\"><span style=\"font-size:12pt\"><span style=\"font-family:Arial,sans-serif\">Bentuk-Bentuk Ancaman Nonmiliter</span></span></h2>\r\n\r\n<ol>\r\n	<li style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-size:12.0pt\">Ancaman Berdimensi Sosial Budaya</span></span></li>\r\n</ol>\r\n\r\n<p style=\"margin-left:47px; margin-right:3px; text-align:justify\"><span style=\"font-size:12pt\">Ancaman sosial budaya bisa berupa isu-isu mengenai kemiskinan, kebodohan, keterbelakangan, serta ketidakadilan yang menjadi dasar timbulnya konflik vertikal antara pemerintah pusat dengan pemerintah daerah, beserta konflik horizontal yakni suku, agama, ras, dan antargolongan (SARA).</span></p>\r\n\r\n<ol start=\"2\">\r\n	<li style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-size:12.0pt\">Ancaman Berdimensi Teknologi Informasi</span></span></li>\r\n</ol>\r\n\r\n<p style=\"margin-left:47px; margin-right:3px; text-align:justify\"><span style=\"font-size:12pt\">Kemajuan akan ilmu pengetahuan dan teknologi yang berkembang dengan sangat pesat serta memberikan manfaat yang sangat besar bagi seluruh masyarakat, namun kejahatan juga terus mengikuti perkembangan tersebut.</span></p>\r\n\r\n<ol start=\"3\">\r\n	<li style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-size:12.0pt\">Ancaman Berdimensi Keselamatan Umum</span></span></li>\r\n</ol>\r\n\r\n<p style=\"margin-left:47px; margin-right:3px; text-align:justify\"><span style=\"font-size:12pt\">Ancaman untuk keselamatan umum bisa terjadi karena bencana alam, misal gempa bumi, gunung meletus, dan tsunami. Ancaman yang disebabkan oleh manusia, misal penggunaan obat-obatan dan penggunaan bahan kimia, pembuangan limbah industri, kebakaran, hingga kecelakaan alat-alat transportasi.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:18px\"><span style=\"color:#2980b9\">Dalam Penjelasan Lebih Lanjut mengenai Kewaspadaan Dini disampaikan oleh Direktur&nbsp;Jenderal Politik dan Pemerintahan Umum Kementerian Dalam Negeri Bahtiar.&nbsp;</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:18px\"><span style=\"color:#2980b9\">Direktur&nbsp;Jenderal Politik dan Pemerintahan Umum Kementerian Dalam Negeri Bahtiar meminta kepala daerah melakukan kewaspadaan dini masyarakat. Tak hanya itu, Bahtiar juga berpesan agar kepala daerah mampu menjaga hubungan baik secara vertikal maupun horizontal.&nbsp;&nbsp;</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:18px\"><span style=\"color:#2980b9\">Hal itu disampaikannya dalam Pembekalan Kepemimpinan Pemerintahan Dalam Negeri bagi Bupati/Wali Kota dan Wakil Bupati/Wakil Wali Kota Tahun 2021 Gelombang II secara daring dari Kantor Kemendagri, Jakarta Pusat, Senin (14/6).<br />\r\nKewaspadaan Dini adalah serangkaian upaya atau tindakan untuk menangkal segala potensi ancaman, tantangan, hambatan dangan gangguan dengan meningkatkan pendeteksian dan pencegahan dini, yang diberdayakan Badan Kesatuan Bangsa dan Politik atau disebut dengan Kesbangpol, yang memiliki tugas sentral sebagai intelejen daerah dalam melaksanakan deteksi dini kewaspadaan di daerah. &ldquo;Saya kira ini penting disampaikan kepada bapak/ibu Bupati dan Walikota, dalam hal kewaspadaan dini masyarakat, kepala daerah itu adalah Kepalanya Forkopimda, kemudian dalam hal melakukan deteksi dini masyarakat, bapak/ibu dibantu Kesbangpol,&rdquo; kata Bahtiar.<br />\r\n&quot;Sudah ada unit yang membantu bapak/ibu, yaitu Kesbangpol, itulah yang membantu urusan pemerintahan umum di Provinsi kabupaten dan kota sehingga bapak/ibu semua harus memaanfaatkan betul peran Kesbangpol,&quot; tambahnya. Bahtiar menambahkan, Kesbangpol menjadi mata-telinga kepala daerah untuk mendeteksi hal-hal yang mengganggu urusan pemerintahan umum.&nbsp; &ldquo;Disamping laporan kepala dinas, camat, lurah, dan lain-lain, ini (Laporan Kesbangpol) menggambarkan keadaan sebenarnya, ada potensi konflik, masalah, dan seterusnya,&rdquo; bebernya. Sentralnya posisi Kesbangpol dalam membantu Kepala Daerah dalam melakukan deteksi dini kewaspadaan masyarakat, dimintanya dapat difungsikan dan diberdayakan dengan baik, serta didukung dengan SDM yang andal dan anggaran yang cukup.&nbsp;<br />\r\n&ldquo;Saya pastikan kepala daerah yang tidak mendayagunakan Kesbangpolnya dengan baik, dia akan mengalami kesulitan dalam hubungan politik, menghambat pembangunan, dan juga dalam menyelenggarakan urusan pemerintahan umum&rdquo; tutup Bahtiar. Tak hanya itu, Bahtiar juga meminta kepala daerah melalui pencegahan dan penanganan konflik sosial, konflik politik dan pemerintahan melalui pengefektifan peran Kesbangpol. Sebab, menurutnya, konflik tersebut akan menghambat pembangunan jika tak dicegah dengan deteksi dini.<br />\r\n<br />\r\nKepala daerah juga dituntut untuk membina hubungan dengan partai politik, DPRD dan kekuatan politik di daerah. &quot;Kepala daerah ini kepalanya Forkopimda, ia harus bisa menjaga hubungan baik dengan DPRD dan kekuatan politik di daerah,&quot; imbuhnya. Kerukunan sosial dan kerukunan umat beragama juga perlu dibina dengan baik. Apalagi bangsa Indonesia memiliki organisasi kemasyarakatan (ormas) yang memiliki latar belakang beragam. Ormas sebagai salah satu bentuk hak konstitusi warga negar pun perlu dibina dengan baik. Jangan sampai, menjamurnya ormas hingga ke tingkat desa/kelurahan menjadi potensi berkembang biaknya pemahaman di luar ideologi Pancasila. &quot;Ada kemerdekaan yang namanya&nbsp;berserikat berkumpul, maka banyak sekali ormas yang berdiri di daerah, ini kalau tidak dikelola dengan baik akan menjadi kelompok-kelompok penekan, maka harus diberikan tugas ke Kesbangpol untuk melakukan pengelolaan kepada ormas-ormas,&quot; tandasnya. Membina hubungan juga perlu dilakukan dengan komunitas intelejen di daerah, untuk dapat mendeteksi segala ancaman dan dalam rangka kewaspadaan dini. Oleh sebab itu, Kepala Daerah diminta untuk dapat membina hubungan secara vertikal maupun horizontal untuk dapat mewujudkan pembangunan yang dapat mensejahterakan rakyatnya.</span></span><br />\r\n<br />\r\n&nbsp;</p>\r\n', '0a3eeae043160be32f01bdbf3bb308c7.png');
INSERT INTO `halaman` (`id`, `judul`, `konten`, `gambar`) VALUES
(5, 'Penanganan Konflik', '<p>&nbsp;</p>\r\n\r\n<table border=\"1\" cellpadding=\"1\" cellspacing=\"1\" style=\"width:500px\">\r\n	<tbody>\r\n		<tr>\r\n			<td style=\"border-bottom:1px solid black; border-left:1px solid black; border-right:1px solid black; border-top:1px solid black; height:10px; width:38px\">\r\n			<p style=\"text-align:center\"><span style=\"font-size:8px\"><span style=\"font-family:Calibri,sans-serif\"><strong><span style=\"font-family:&quot;Arial&quot;,&quot;sans-serif&quot;\">NO</span></strong></span></span></p>\r\n			</td>\r\n			<td style=\"border-bottom:1px solid black; border-left:none; border-right:1px solid black; border-top:1px solid black; height:10px; width:62px\">\r\n			<p style=\"text-align:center\"><span style=\"font-size:8px\"><span style=\"font-family:Calibri,sans-serif\"><strong><span style=\"font-family:&quot;Arial&quot;,&quot;sans-serif&quot;\">BIDANG</span></strong></span></span></p>\r\n			</td>\r\n			<td style=\"border-bottom:1px solid black; border-left:none; border-right:1px solid black; border-top:1px solid black; height:10px; width:136px\">\r\n			<p style=\"text-align:center\"><span style=\"font-size:8px\"><span style=\"font-family:Calibri,sans-serif\"><strong><span style=\"font-family:&quot;Arial&quot;,&quot;sans-serif&quot;\">URAIAN PERMASALAHAN</span></strong></span></span></p>\r\n			</td>\r\n			<td style=\"border-bottom:1px solid black; border-left:none; border-right:1px solid black; border-top:1px solid black; height:10px; width:121px\">\r\n			<p style=\"text-align:center\"><span style=\"font-size:8px\"><span style=\"font-family:Calibri,sans-serif\"><strong><span style=\"font-family:&quot;Arial&quot;,&quot;sans-serif&quot;\">KERAWANAN</span></strong></span></span></p>\r\n			</td>\r\n			<td style=\"border-bottom:1px solid black; border-left:none; border-right:1px solid black; border-top:1px solid black; height:10px; width:139px\">\r\n			<p style=\"text-align:center\"><span style=\"font-size:8px\"><span style=\"font-family:Calibri,sans-serif\"><strong><span style=\"font-family:&quot;Arial&quot;,&quot;sans-serif&quot;\">UPAYA YANG TELAH DILAKUKAN</span></strong></span></span></p>\r\n			</td>\r\n			<td style=\"border-bottom:1px solid black; border-left:none; border-right:1px solid black; border-top:1px solid black; height:10px; width:153px\">\r\n			<p style=\"text-align:center\"><span style=\"font-size:8px\"><span style=\"font-family:Calibri,sans-serif\"><strong><span style=\"font-family:&quot;Arial&quot;,&quot;sans-serif&quot;\">RENCANA TINDAK LANJUT</span></strong></span></span></p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td style=\"border-bottom:1px solid black; border-left:1px solid black; border-right:1px solid black; border-top:none; height:10px; width:38px\">\r\n			<p style=\"text-align:center\"><span style=\"font-size:8px\"><span style=\"font-family:Calibri,sans-serif\"><strong><span style=\"font-family:&quot;Arial&quot;,&quot;sans-serif&quot;\">1</span></strong></span></span></p>\r\n			</td>\r\n			<td style=\"border-bottom:1px solid black; border-left:none; border-right:1px solid black; border-top:none; height:10px; width:62px\">\r\n			<p style=\"text-align:center\"><span style=\"font-size:8px\"><span style=\"font-family:Calibri,sans-serif\"><strong><span style=\"font-family:&quot;Arial&quot;,&quot;sans-serif&quot;\">2</span></strong></span></span></p>\r\n			</td>\r\n			<td style=\"border-bottom:1px solid black; border-left:none; border-right:1px solid black; border-top:none; height:10px; width:136px\">\r\n			<p style=\"text-align:center\"><span style=\"font-size:8px\"><span style=\"font-family:Calibri,sans-serif\"><strong><span style=\"font-family:&quot;Arial&quot;,&quot;sans-serif&quot;\">3</span></strong></span></span></p>\r\n			</td>\r\n			<td style=\"border-bottom:1px solid black; border-left:none; border-right:1px solid black; border-top:none; height:10px; width:121px\">\r\n			<p style=\"text-align:center\"><span style=\"font-size:8px\"><span style=\"font-family:Calibri,sans-serif\"><strong><span style=\"font-family:&quot;Arial&quot;,&quot;sans-serif&quot;\">4</span></strong></span></span></p>\r\n			</td>\r\n			<td style=\"border-bottom:1px solid black; border-left:none; border-right:1px solid black; border-top:none; height:10px; width:139px\">\r\n			<p style=\"text-align:center\"><span style=\"font-size:8px\"><span style=\"font-family:Calibri,sans-serif\"><strong><span style=\"font-family:&quot;Arial&quot;,&quot;sans-serif&quot;\">5</span></strong></span></span></p>\r\n			</td>\r\n			<td style=\"border-bottom:1px solid black; border-left:none; border-right:1px solid black; border-top:none; height:10px; width:153px\">\r\n			<p style=\"text-align:center\"><span style=\"font-size:8px\"><span style=\"font-family:Calibri,sans-serif\"><strong><span style=\"font-family:&quot;Arial&quot;,&quot;sans-serif&quot;\">6</span></strong></span></span></p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td colspan=\"6\" style=\"border-bottom:1px solid black; border-left:1px solid black; border-right:1px solid black; border-top:none; height:23px; width:650px\">\r\n			<p style=\"text-align:center\"><span style=\"font-size:8px\"><span style=\"font-family:Calibri,sans-serif\"><strong><span style=\"font-family:&quot;Arial&quot;,&quot;sans-serif&quot;\">RES LUBUKLINGGAU</span></strong></span></span></p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td style=\"border-bottom:1px solid black; border-left:1px solid black; border-right:1px solid black; border-top:none; height:57px; vertical-align:top; width:38px\">\r\n			<p style=\"text-align:center\">&nbsp;</p>\r\n\r\n			<p style=\"text-align:center\">&nbsp;</p>\r\n\r\n			<p style=\"text-align:center\">&nbsp;</p>\r\n\r\n			<p style=\"text-align:center\">&nbsp;</p>\r\n			</td>\r\n			<td style=\"border-bottom:1px solid black; border-left:none; border-right:1px solid black; border-top:none; height:57px; vertical-align:top; width:62px\">\r\n			<p style=\"text-align:center\">&nbsp;</p>\r\n\r\n			<p style=\"text-align:center\"><span style=\"font-size:8px\"><span style=\"font-family:Calibri,sans-serif\"><strong><span style=\"font-family:&quot;Arial&quot;,&quot;sans-serif&quot;\">SOSIAL</span></strong></span></span></p>\r\n			</td>\r\n			<td style=\"border-bottom:1px solid black; border-left:none; border-right:1px solid black; border-top:none; height:57px; vertical-align:top; width:136px\">\r\n			<p style=\"margin-left:21px; margin-right:2px; text-align:justify\">&nbsp;</p>\r\n\r\n			<p style=\"margin-left:12px; margin-right:2px; text-align:justify\"><span style=\"font-size:8px\"><span style=\"font-family:Calibri,sans-serif\"><strong><span style=\"font-family:&quot;Arial&quot;,&quot;sans-serif&quot;\">PERMASALAHAN KONFLIK SENGKETA LAHAN EKS. PT. CIKENCRENG.</span></strong></span></span></p>\r\n\r\n			<p style=\"margin-left:12px; margin-right:2px; text-align:justify\">&nbsp;</p>\r\n\r\n			<ol>\r\n				<li style=\"text-align:justify\"><span style=\"font-size:8px\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-family:&quot;Arial&quot;,&quot;sans-serif&quot;\">Permasalahan Konflik Sengketa lahan Eks. PT. Cikencreng<span style=\"background-color:white\"><span style=\"color:#333333\">denganluas<strong>&plusmn;1.245 HA</strong>yang</span></span> melibatkan Eks. PT. Cikencreng, Pemkot Lubuklinggau dan Warga yang menggarap diatas lahan Eks. PT. Cikencreng Kec. Lubuklinggau Utara I. </span></span></span></li>\r\n			</ol>\r\n\r\n			<p style=\"margin-left:31px; margin-right:2px; text-align:justify\">&nbsp;</p>\r\n\r\n			<ol start=\"2\">\r\n				<li style=\"text-align:justify\"><span style=\"font-size:8px\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-family:&quot;Arial&quot;,&quot;sans-serif&quot;\">Dari permasalahan tersebut PT. Cikencreng melakukan gugatan kepengadilan Negeri Kota Lubuklinggau <strong>No. 40 / Pdt.G / 2017 / PN.LLG</strong> terkait sengketa lahan eks. PT. Cikencreng di kecamatan Lubuklinggau Utara I.</span></span></span></li>\r\n			</ol>\r\n\r\n			<p style=\"margin-left:31px; margin-right:2px; text-align:justify\">&nbsp;</p>\r\n\r\n			<ol start=\"3\">\r\n				<li style=\"text-align:justify\"><span style=\"font-size:8px\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-family:&quot;Arial&quot;,&quot;sans-serif&quot;\">Permasalahan tersebut dimenangkan oleh Pihak PT. Cikencreng namun dengan kesepakatan sebagai berikut :</span></span></span></li>\r\n			</ol>\r\n\r\n			<ul>\r\n				<li style=\"text-align:justify\"><span style=\"font-size:8px\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-family:&quot;Arial&quot;,&quot;sans-serif&quot;\">PT. Cikencreng seluas <span style=\"background-color:white\"><span style=\"color:#333333\">Kurleb<strong>631 Ha</strong></span></span></span></span></span></li>\r\n				<li style=\"text-align:justify\"><span style=\"font-size:8px\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-family:&quot;Arial&quot;,&quot;sans-serif&quot;\">Pemkot Lubuklinggau seluas Kurleb <strong>534 Ha</strong></span></span></span></li>\r\n				<li style=\"text-align:justify\"><span style=\"font-size:8px\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-family:&quot;Arial&quot;,&quot;sans-serif&quot;\">Relokasi warga <span style=\"background-color:white\"><span style=\"color:#333333\">seluasKurleb<strong>100 Ha</strong>dan cadanganKurleb <strong>20 Ha</strong>.</span></span></span></span></span></li>\r\n			</ul>\r\n\r\n			<p style=\"margin-left:50px; margin-right:2px; text-align:justify\">&nbsp;</p>\r\n\r\n			<ol start=\"4\">\r\n				<li style=\"text-align:justify\"><span style=\"font-size:8px\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-family:&quot;Arial&quot;,&quot;sans-serif&quot;\">Adapun <span style=\"color:#323233\">Data Masyarakat yang menggarapdiLahanEks. PT. Cikencrengsebagaiberikut :</span></span></span></span></li>\r\n			</ol>\r\n\r\n			<ul>\r\n				<li style=\"text-align:justify\"><span style=\"font-size:8px\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-family:&quot;Arial&quot;,&quot;sans-serif&quot;\"><span style=\"color:#323233\">KelurahanPetanangIlirRt 1 &amp; 2 sebanyak<strong>89 orangpenduduk</strong> yang menempatidilokasiEks. PT. Cikencreng.</span></span></span></span></li>\r\n			</ul>\r\n\r\n			<p style=\"margin-left:50px; margin-right:2px; text-align:justify\">&nbsp;</p>\r\n\r\n			<ul>\r\n				<li style=\"text-align:justify\"><span style=\"font-size:8px\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-family:&quot;Arial&quot;,&quot;sans-serif&quot;\"><span style=\"color:#323233\">KelurahanPetanangUluRt 1 &amp;2&nbsp; sebanyak<strong>73 orangpenduduk</strong> yang menempatidilokasiEks. PT. Cikencrengterdiridari<strong>52 Bangunan, 18 Tanah Kosong, 3 Pertanian</strong>.</span></span></span></span></li>\r\n			</ul>\r\n\r\n			<p style=\"margin-left:50px; margin-right:2px; text-align:justify\">&nbsp;</p>\r\n\r\n			<ul>\r\n				<li style=\"text-align:justify\"><span style=\"font-size:8px\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-family:&quot;Arial&quot;,&quot;sans-serif&quot;\"><span style=\"color:#323233\">KelurahanSumberAgungsebanyak<strong>59 orangpenduduk</strong> yang menempatidilokasiEks. PT. Cikencrengterdiridari<strong>35 BangunanRumah Permanent dan 24 lahanKebun.</strong></span></span></span></span></li>\r\n			</ul>\r\n\r\n			<p style=\"margin-left:50px; margin-right:2px; text-align:justify\">&nbsp;</p>\r\n\r\n			<ul>\r\n				<li style=\"text-align:justify\"><span style=\"font-size:8px\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-family:&quot;Arial&quot;,&quot;sans-serif&quot;\"><span style=\"color:#323233\">KelurahanBelalau II sebanyak<strong>164 orangpenduduk</strong> yang menempatidilokasiEks. PT. Cikencrengterdiridari<strong>60 BangunanRumahKaryawanEks. PT. Cikencreng, 68 BangunanRumahwargapendatang, 36 Rumahdanlahangarapan.</strong></span></span></span></span></li>\r\n			</ul>\r\n\r\n			<p style=\"margin-left:50px; margin-right:2px; text-align:justify\">&nbsp;</p>\r\n\r\n			<ul>\r\n				<li style=\"text-align:justify\"><span style=\"font-size:8px\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-family:&quot;Arial&quot;,&quot;sans-serif&quot;\"><span style=\"color:#323233\">Bahwahasilpendataansementaraolehpihakkecamatandankelurahan, diperkirakanjumlahrumahdilahanCikencrengsebanyak<strong>340 rumah</strong>baikpermanenmaupun semi permanen, dansebanyak<strong>170 penggarap</strong></span></span></span></span></li>\r\n			</ul>\r\n\r\n			<p style=\"margin-left:31px; margin-right:2px; text-align:justify\">&nbsp;</p>\r\n\r\n			<ol start=\"5\">\r\n				<li style=\"text-align:justify\"><span style=\"font-size:8px\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-family:&quot;Arial&quot;,&quot;sans-serif&quot;\">Bahwa PadahariRabutanggal 7 Juli 2021 pukul 09.50 wibbertempatdiRuang Op. DayangTorekPemkotLubuklinggautelahdilaksanakangiatRapatKoordinasipersiapanpemasangantampal batasdilahanEks. PT. Cikencreng yang dipimpin oleh Sekda Kota Lubuklinggau dan dihadiri oleh <span style=\"color:#323233\">Sdr. ArifHidayat selaku Manager PT. Cikencreng dengan hasil kesepakatan merencanakan pengukuran dan pemasangan tampal batas di Lahan Eks. PT. Cikencreng pada hari Jum&rsquo;at tanggal 23 Juli 2021.</span></span></span></span></li>\r\n			</ol>\r\n\r\n			<p style=\"margin-left:31px; margin-right:2px; text-align:justify\">&nbsp;</p>\r\n\r\n			<ol start=\"6\">\r\n				<li style=\"text-align:justify\"><span style=\"font-size:8px\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-family:&quot;Arial&quot;,&quot;sans-serif&quot;\">Bahwa rencana kegiatan pengukuran dan pemasangan tampal batas di lahan eks. PT. Cikencreng tersebut <strong>DITUNDA </strong>dikarenakan terdapat penolakan dilapangan dari Warga yang menggarap dilahan Eks. PT. Cikencreng.</span></span></span></li>\r\n			</ol>\r\n\r\n			<p style=\"margin-left:31px; margin-right:2px; text-align:justify\">&nbsp;</p>\r\n			</td>\r\n			<td style=\"border-bottom:1px solid black; border-left:none; border-right:1px solid black; border-top:none; height:57px; vertical-align:top; width:121px\">\r\n			<p style=\"margin-left:21px; text-align:justify\">&nbsp;</p>\r\n\r\n			<ol>\r\n				<li style=\"text-align:justify\"><span style=\"font-size:8px\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-family:&quot;Arial&quot;,&quot;sans-serif&quot;\">Akan terjadinya penolakan dari Warga yang menggarap di atas lahan Eks. PT. Cikencreng terhadap&nbsp; pihak PT. Cikencreng .</span></span></span></li>\r\n			</ol>\r\n\r\n			<p style=\"margin-left:21px; margin-right:2px; text-align:justify\">&nbsp;</p>\r\n\r\n			<ol start=\"2\">\r\n				<li style=\"text-align:justify\"><span style=\"font-size:8px\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-family:&quot;Arial&quot;,&quot;sans-serif&quot;\">Akan terjadi bentrok dilapangan saat pengukuran ataupun penggusuran antara pihak keamanan dan warga yang menggarap diatas lahan PT. Eks. PT. Cikencreng apabila belum terdapat kejelasan ganti rugi tanam tumbuh <span style=\"color:black\">yang selama ini telah dikelola oleh </span>&nbsp;warga.</span></span></span></li>\r\n			</ol>\r\n\r\n			<p style=\"margin-left:21px; margin-right:2px; text-align:justify\">&nbsp;</p>\r\n\r\n			<ol start=\"3\">\r\n				<li style=\"text-align:justify\"><span style=\"font-size:8px\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-family:&quot;Arial&quot;,&quot;sans-serif&quot;\">Apabila lambat dalam penanganan konflik tersebut, Akan lebih banyak lagi warga yang mengakui hak tanam tumbuh atas kepemilikan lahan di Eks. PT. Cikencreng.</span></span></span></li>\r\n			</ol>\r\n\r\n			<p style=\"margin-left:21px; text-align:justify\">&nbsp;</p>\r\n\r\n			<p style=\"margin-left:21px; text-align:justify\">&nbsp;</p>\r\n			</td>\r\n			<td style=\"border-bottom:1px solid black; border-left:none; border-right:1px solid black; border-top:none; height:57px; vertical-align:top; width:139px\">\r\n			<p style=\"margin-right:2px; text-align:justify\">&nbsp;</p>\r\n\r\n			<ol>\r\n				<li style=\"text-align:justify\"><span style=\"font-size:8px\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-family:&quot;Arial&quot;,&quot;sans-serif&quot;\">Telah dilakukan pengamanan terbuka dan tertutup saat pelaksanaan sidang di pengadilan Negeri Kota Lubuklinggau, baik hasil keputusan maupun kegiatan mediasai.</span></span></span></li>\r\n			</ol>\r\n\r\n			<p style=\"margin-left:21px; text-align:justify\">&nbsp;</p>\r\n\r\n			<ol start=\"2\">\r\n				<li style=\"text-align:justify\"><span style=\"font-size:8px\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-family:&quot;Arial&quot;,&quot;sans-serif&quot;\">Telah dilakukan Monitoring pelaksanaan rapat koordinasi terkait pembahasan kegiatan di lahan Eks. PT. Cikencreng.</span></span></span></li>\r\n			</ol>\r\n\r\n			<p style=\"margin-left:21px; text-align:justify\">&nbsp;</p>\r\n\r\n			<ol start=\"3\">\r\n				<li style=\"text-align:justify\"><span style=\"font-size:8px\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-family:&quot;Arial&quot;,&quot;sans-serif&quot;\">Telah dilakukan Koordinasi dengan Camat Lubuklinggau Utara 1 dan Kelurahan yang terdapat warganya menduduki lahan Eks. PT. Cikencreng.</span></span></span></li>\r\n			</ol>\r\n\r\n			<p style=\"margin-left:21px; text-align:justify\">&nbsp;</p>\r\n\r\n			<ol start=\"4\">\r\n				<li style=\"text-align:justify\"><span style=\"font-size:8px\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-family:&quot;Arial&quot;,&quot;sans-serif&quot;\">Pada hari Jumat Tanggal 23 Juli 2021 sekira Pukul 10.30 wib telah di lakukan rapat koordinasi giat pengamanan pengukuran Lahan PT. CIKENCRENG di kec. Lubuklinggau Utara I kota lubuk Linggau. Di kantor camat Lubuklinggau Utara I yang dihadiri oleh Asisten I pemerintah kota Lubuklinggau Bpk Kahlan Bahar, Kapolsek Lubuklinggau Utara AKP. Sudarno, Camat Lubuklinggau Utara Dedi Dores, SH. Pihak PT CIKENCRENG, personil PAM giat sebanyak 10 ( sepuluh ) orang.</span></span></span></li>\r\n			</ol>\r\n\r\n			<p style=\"margin-left:21px; text-align:justify\">&nbsp;</p>\r\n\r\n			<ol start=\"5\">\r\n				<li style=\"text-align:justify\"><span style=\"font-size:8px\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-family:&quot;Arial&quot;,&quot;sans-serif&quot;\">Pada saat pelaksanaan giat di kelurahan Sumber Agung,&nbsp; giat tersebut mendapatkan penolakan oleh warga sehingga giat&nbsp; pengukuran lahan PT Cikencreng <strong>DITUNDA</strong>.</span></span></span></li>\r\n			</ol>\r\n\r\n			<p style=\"margin-left:21px; text-align:justify\">&nbsp;</p>\r\n\r\n			<p style=\"margin-left:21px; text-align:justify\">&nbsp;</p>\r\n\r\n			<ol start=\"6\">\r\n				<li style=\"text-align:justify\"><span style=\"font-size:8px\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-family:&quot;Arial&quot;,&quot;sans-serif&quot;\">PihakPemerintahkotaLubuklinggau, BPN kotaLubuklinggau, pihakEks. PT. Cikencrengsertainstansiterkaitlainnyaberupayamencarisolusiterkaitlahandanasetmilik PT. Cikencreng yang saatiniditempatidandigarapolehwargamasyarakatbaik yang tinggaldilokasitersebutmaupun yang mengklaimlahantersebut.</span></span></span></li>\r\n			</ol>\r\n\r\n			<p style=\"margin-left:21px; text-align:justify\">&nbsp;</p>\r\n\r\n			<p style=\"margin-left:21px; text-align:justify\">&nbsp;</p>\r\n\r\n			<p style=\"margin-left:21px; margin-right:2px; text-align:justify\">&nbsp;</p>\r\n			</td>\r\n			<td style=\"border-bottom:1px solid black; border-left:none; border-right:1px solid black; border-top:none; height:57px; vertical-align:top; width:153px\">\r\n			<p style=\"margin-left:21px; text-align:justify\">&nbsp;</p>\r\n\r\n			<ol>\r\n				<li style=\"text-align:justify\"><span style=\"font-size:8px\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-family:&quot;Arial&quot;,&quot;sans-serif&quot;\">Bahwa saat ini Pemerintah kota Lubuklinggau masih mendata masyarakat yang mengklaim <span style=\"color:black\">hak tumbuh tanam atas lahaneks. PT. Cikencreng.</span></span></span></span></li>\r\n			</ol>\r\n\r\n			<p style=\"margin-left:12px; text-align:justify\">&nbsp;</p>\r\n\r\n			<ol start=\"2\">\r\n				<li style=\"text-align:justify\"><span style=\"font-size:8px\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-family:&quot;Arial&quot;,&quot;sans-serif&quot;\">Akan tetap dilakukan Monitoring perkembangan yang terjadi di masyarakat yang menggarap lahan eks. PT. Cikencreng.</span></span></span></li>\r\n			</ol>\r\n\r\n			<p style=\"margin-left:12px; margin-right:2px; text-align:justify\">&nbsp;</p>\r\n\r\n			<ol start=\"3\">\r\n				<li style=\"text-align:justify\"><span style=\"font-size:8px\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-family:&quot;Arial&quot;,&quot;sans-serif&quot;\">Melakukan Koordinasi dengan Pihak Pemkot Lubuklinggau dan PT. Cikencreng untuk mendapatkan win win solution atas permasalahan relokasi yang akan di berikan kepada warga yang menggarap. </span></span></span></li>\r\n			</ol>\r\n\r\n			<p style=\"margin-left:12px; margin-right:2px; text-align:justify\">&nbsp;</p>\r\n\r\n			<ol start=\"4\">\r\n				<li style=\"text-align:justify\"><span style=\"font-size:8px\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-family:&quot;Arial&quot;,&quot;sans-serif&quot;\">Melakukan pemantauan perkembangan Permasalahan Konflik Sengketa lahan Eks. PT. Cikencreng, Pemkot Lubuklinggau dan Warga yang menggarap diatas lahan Eks. PT. Cikencreng.</span></span></span></li>\r\n			</ol>\r\n\r\n			<p style=\"margin-left:12px; margin-right:2px; text-align:justify\">&nbsp;</p>\r\n\r\n			<ol start=\"5\">\r\n				<li style=\"text-align:justify\"><span style=\"font-size:8px\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-family:&quot;Arial&quot;,&quot;sans-serif&quot;\">Mengedepankan Bhabinkamtibmas setempat untukmemberikanhimbauan secara humanisagar&nbsp; tidak terjadi bentrok dilapangan saat pengukuran ataupun penggusuran antara pihak keamanan dan warga yang menggarap diatas lahan PT. Eks. PT. Cikencreng apabila belum terdapat kejelasan ganti rugi tanam tumbuh <span style=\"color:black\">yang selama ini telah dikelola oleh </span>&nbsp;warga.</span></span></span></li>\r\n			</ol>\r\n\r\n			<p style=\"margin-left:2px; text-align:justify\">&nbsp;</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>&nbsp;</td>\r\n			<td>&nbsp;</td>\r\n			<td>&nbsp;</td>\r\n			<td>&nbsp;</td>\r\n			<td>&nbsp;</td>\r\n			<td>&nbsp;</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<p>&nbsp;</p>\r\n', '476f239beb60e13573c0b63feeebb241.jpg'),
(6, 'PESERTA PEMILU 2019', '<h2>Partai Politik Peserta Pemilu 2019</h2>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Berdasarkan Keputusan Komisi Pemilihan Umum:</p>\r\n\r\n<ol>\r\n	<li>Nomor&nbsp;<a href=\"https://jdih.kpu.go.id/data/data_kepkpu/58_SK%20No.%2058%202018.PDF\">58/PL.01.1-Kpt/03/KPU/II/2018</a>&nbsp;&nbsp;tentang Penetapan Partai Politik Peserta Pemilihan Umum Anggota Dewan Perwakilan Rakyat, Dewan Perwakilan Rakyat Daerah Provinsi dan Dewan Perwakilan Rakyat Daerah Kabupaten/Kota Tahun 2019</li>\r\n	<li>Nomor&nbsp;<a href=\"https://jdih.kpu.go.id/data/data_kepkpu/80_Keputusan%20KPU.pdf\">80/PL.01.1-Kpt/03/KPU/III/2018</a>&nbsp;&nbsp;tentang Perubahan Keputusan Komisi Pemilihan Umum Nomor 58/PL.01.1-Kpt/03/Kpu/II/2018 tentang Penetapan Partai Politik Peserta Pemilihan Umum Anggota Dewan Perwakilan Rakyat, Dewan Perwakilan Rakyat Daerah Provinsi dan Dewan Perwakilan Rakyat Daerah Kabupaten/Kota Tahun 2019</li>\r\n	<li>Nomor&nbsp;<a href=\"https://jdih.kpu.go.id/data/data_kepkpu/SK%20no%20309.pdf\">309/PL.01.1-Kpt/03/KPU/IV/2018</a>&nbsp;&nbsp;tentang Perubahan Kedua atas Keputusan Komisi Pemilihan Umum Nomor 58/PL.01.1-Kpt/03/KPU/II/2018 tentang Penetapan Partai Politik Peserta Pemilihan Umum Anggota Dewan Perwakilan Rakyat, Dewan Perwakilan Rakyat Daerah Provinsi dan Dewan Perwakilan Rakyat Daerah Kabupaten/Kota Tahun 2019</li>\r\n</ol>\r\n\r\n<table border=\"1\" cellpadding=\"1\" cellspacing=\"1\" style=\"height:499px; width:100%\">\r\n	<tbody>\r\n		<tr>\r\n			<td><strong>NAMA PARTAI</strong></td>\r\n			<td><strong>FILE BERKAS</strong></td>\r\n		</tr>\r\n		<tr>\r\n			<td>Partai Kebangkitan Bangsa</td>\r\n			<td><a href=\"http://kesbangpol.lubuklinggaukota.go.id/assets/berkas/c09ea1e58017a8dbb9dcc1197f1e4696.pdf\">LIHAT BERKAS</a></td>\r\n		</tr>\r\n		<tr>\r\n			<td>Partai Gerakan Indonesia Raya</td>\r\n			<td><a href=\"http://kesbangpol.lubuklinggaukota.go.id/assets/berkas/b70da6efc2e12355973fdacaa8f7d17b.pdf\">LIHAT BERKAS</a></td>\r\n		</tr>\r\n		<tr>\r\n			<td>Partai Demokrasi Indonesia Perjuangan</td>\r\n			<td><a href=\"http://kesbangpol.lubuklinggaukota.go.id/assets/berkas/b47a811bec93ef13e3c58b3adf71cc72.pdf\">LIHAT BERKAS</a></td>\r\n		</tr>\r\n		<tr>\r\n			<td>Partai Golongan Karya</td>\r\n			<td><a href=\"http://kesbangpol.lubuklinggaukota.go.id/assets/berkas/3783832f149a2606226648088b191856.pdf\">LIHAT BERKAS</a></td>\r\n		</tr>\r\n		<tr>\r\n			<td>Partai Nasional Demokrat</td>\r\n			<td><a href=\"http://kesbangpol.lubuklinggaukota.go.id/assets/berkas/9429f24f153a76fa5349d190c3e390f5.pdf\">LIHAT BERKAS</a></td>\r\n		</tr>\r\n		<tr>\r\n			<td>Partai Garuda</td>\r\n			<td><a href=\"http://kesbangpol.lubuklinggaukota.go.id/assets/berkas/3c2a006abb51382745b750a8351f0a2d.pdf\">LIHAT BERKAS</a></td>\r\n		</tr>\r\n		<tr>\r\n			<td>Partai Berkarya</td>\r\n			<td><a href=\"http://kesbangpol.lubuklinggaukota.go.id/assets/berkas/8a6c8cba8a860c16d8a7598647a934e5.pdf\">LIHAT BERKAS</a></td>\r\n		</tr>\r\n		<tr>\r\n			<td>Partai Keadilan Sejahtera</td>\r\n			<td><a href=\"http://kesbangpol.lubuklinggaukota.go.id/assets/berkas/846817b39e1a261b8c6dc2b66d1c66b6.pdf\">LIHAT BERKAS</a></td>\r\n		</tr>\r\n		<tr>\r\n			<td>Partai Persatuan Indonesia</td>\r\n			<td><a href=\"http://kesbangpol.lubuklinggaukota.go.id/assets/berkas/3ed36d20579b4a3ead94a6b6e0c1a645.pdf\">LIHAT BERKAS</a></td>\r\n		</tr>\r\n		<tr>\r\n			<td>Partai Persatuan Pembangunan</td>\r\n			<td><a href=\"http://kesbangpol.lubuklinggaukota.go.id/assets/berkas/f5b3028c429d9a9d079ff95cf2c005a5.pdf\">LIHAT BERKAS</a></td>\r\n		</tr>\r\n		<tr>\r\n			<td>Partai Solidaritas Indonesia</td>\r\n			<td><a href=\"http://kesbangpol.lubuklinggaukota.go.id/assets/berkas/c09ea1e58017a8dbb9dcc1197f1e4696.pdf\">LIHAT BERKAS</a></td>\r\n		</tr>\r\n		<tr>\r\n			<td>Partai Amanat Nasional</td>\r\n			<td><a href=\"http://kesbangpol.lubuklinggaukota.go.id/assets/berkas/c72883b1e874f9998305745e2db73e16.pdf\">LIHAT BERKAS</a></td>\r\n		</tr>\r\n		<tr>\r\n			<td>Partai Hati Nurani Rakyat</td>\r\n			<td><a href=\"http://kesbangpol.lubuklinggaukota.go.id/assets/berkas/23e41f07418d127cf6f2ff4350cd0bf9.pdf\">LIHAT BERKAS</a></td>\r\n		</tr>\r\n		<tr>\r\n			<td>Partai Demokrat</td>\r\n			<td><a href=\"http://kesbangpol.lubuklinggaukota.go.id/assets/berkas/91fbcbb4d064ddceead9e31619c80e59.pdf\">LIHAT BERKAS</a></td>\r\n		</tr>\r\n		<tr>\r\n			<td>Partai Bulan Bintang</td>\r\n			<td><a href=\"http://kesbangpol.lubuklinggaukota.go.id/assets/berkas/a35f3cbed11cf4c88acefd90af28a0e7.pdf\">LIHAT BERKAS</a></td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n', '828794dac0450034656dc8faf0b46229.png'),
(7, 'Perolehan Suara Pemilu DPRD Kota Lubuklinggau 2019-2024', '', '572d9f597ef9efd542b36ee337d0b411.png'),
(8, 'Perolehan Kursi', '<p>&nbsp;Komisi Pemilihan Umum (KPU) Kota Lubuklinggau menyelesaikan rekapitulasi perolehan suara calon anggota Dewan Perwakilan Rakyat Daerah (DPRD) Lubuklinggau, Jumat (3/2019) dini hari sekitar pukul 04.30 WIB, di Bandiklat Lubuklinggau.</p>\r\n\r\n<p>Berdasarkan sertifikat model DB1-DPRD KAB/KOTA rekapitulasi tingkat KPU Lubuklinggau perolehan suara Caleg Lubuklinggau, dimana Partai Golkar unggul dengan perolehan suara 20.672 dengan lima kursi, disusul Gerindra 20.08 juga meraih lima kursi, sementara PDI P&nbsp; juga meraih lima kursi mendapat total suara 15.128.</p>\r\n', '54d4015c117867c9407fdc211126e7fb.png'),
(9, 'Aturan Hukum', '<p>-Peraturan Wali Kota (PERWALI) Kota Lubuklinggau No 31 Tahun 2019 &nbsp;tentang Kedudukan Susunan Organisasi Tugas dan Fungsi Serta Tata Kerja Badan Kesatuan Bangsa&nbsp; &nbsp; &nbsp;dan&nbsp; Politik Kota Lubuklinggau.</p>\r\n\r\n<p>-Permendagri Nomor 11 Tahun 2019 yang mengatur tentang pelaksanaan urusan perangkat daerah Bidang Kesbangpol.</p>\r\n\r\n<p><strong>DAFTAR PUSTAKA</strong></p>\r\n\r\n<p>1.&nbsp;&nbsp; &nbsp;BUKU</p>\r\n\r\n<p>&nbsp; &nbsp; &nbsp;1.&nbsp;&nbsp;Anonim, Marketing Sektor Publik, Jakarta: Pelatihan Kepemimpinan Nasional Tingkat II, 2019.</p>\r\n\r\n<p><br />\r\n&nbsp; &nbsp; &nbsp; 2.&nbsp;&nbsp;Anonim, Panduan Monitoring &amp; Coaching Pelatihan Kepemimpinan Nasional Tingkat II Angkatan III&nbsp; Tahun 2019, Jakarta: Pusat Pengembangan Kompetensi Kepemimpinan Nasional dan Manajerial ASN Lembaga Administrasi Negara,2019.</p>\r\n\r\n<p><br />\r\n&nbsp; &nbsp; &nbsp; 3.&nbsp;&nbsp; Anonim, Proyek Perubahan Pelatihan Kepemimpinan Nasional Tingkat II, Jakarta: Pusat Pengembangan Kompetensi Kepemimpinan dan Manjerial ASN, Lembaga Sandi Negara, 2019.</p>\r\n\r\n<p><br />\r\n2.&nbsp;&nbsp; &nbsp;INTERNET<br />\r\n1.&nbsp;&nbsp; &nbsp;http.www.google.com/wikipedia-definisi-dan-istilah-radikal&nbsp;<br />\r\n2.&nbsp;&nbsp; &nbsp;http.www.google.com/definisi-tim-kewaspadaan-dini-daerah</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>3.&nbsp;&nbsp; &nbsp;PERATURAN PERUNDANG-UNDANGAN</p>\r\n\r\n<p><br />\r\n1.&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;Peraturan Menteri Dalam Negeri Nomor 46 Tahun 2019 Tentang Perubahan Atas Peraturan Menteri Dalam Negeri Nomor 2 Tahun 2018 Tentang Kewaspadaan Dini Di Daerah<br />\r\n2.&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;Peraturan Menteri Dalam Negeri Nomor 57 Tahun 2017 Tentang Pendaftaran dan Pengelolaan Sistem Informasi Organisasi Kemasyarakatan;<br />\r\n3.&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;Peraturan Wali Kota Nomor 31 Tahun 2019 tentang Kedudukan Susunan Organisasi Tugas dan Fungsi Serta Tata Kerja Badan Kesatuan Bangsa dan Politik Kota Lubuklinggau<br />\r\n4.&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;Keputusan Wali Kota Lubuklinggau Nomor 129/KPTS/BAKESBANGPOL/2021 tentang Penetapan Tim Kewaspadaan Dini Pemerintah Daerah Kota Lubuklinggau 2021</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n', '82347af0bc435c797028d666343d9cac.png'),
(10, 'Pernyataan Komitmen Bersama', '', 'a288a003c52f08cac5bb076e6da20b98.PNG'),
(11, 'Foto Kegiatan', '<p><img alt=\"\" src=\"https://i.ibb.co/GRPMHFB/186506442-538724117497936-8668861647995208096-n.jpg\" style=\"height:200px; width:315px\" /><img alt=\"\" src=\"https://i.ibb.co/6wML86J/186556767-538723714164643-8454528140345006084-n-1.jpg\" style=\"height:200px; width:315px\" /><img alt=\"\" src=\"https://i.ibb.co/GVFyH8y/186479348-538723767497971-4916935752276007628-n.jpg\" style=\"height:200px; width:315px\" /><img alt=\"\" src=\"https://i.ibb.co/XkFKxP6/201669057-558904032146611-8368820678496194669-n.jpg\" style=\"height:200px; width:315px\" /><img alt=\"\" src=\"https://i.ibb.co/PW6xZL5/202073834-558903992146615-3927045041020953410-n.jpg\" style=\"height:200px; width:315px\" /><img alt=\"\" src=\"https://i.ibb.co/1TYngwC/240684459-599572948079719-550461045291616548-n.jpg\" style=\"height:200px; width:315px\" /><img alt=\"\" src=\"https://i.ibb.co/2tpJXSq/240457678-599572898079724-5060742044945898405-n.jpg\" style=\"height:200px; width:315px\" /><img alt=\"\" src=\"https://i.ibb.co/Q9XD3x9/242196984-617101909660156-5945993346523581349-n.jpg\" style=\"height:200px; width:315px\" /><img alt=\"\" src=\"https://i.ibb.co/hZKGqJw/242685145-620896305947383-5343507077694780255-n.jpg\" style=\"height:200px; width:315px\" /><img alt=\"\" src=\"https://i.ibb.co/nPK6sYM/242700061-620895972614083-1452720538169343956-n.jpg\" style=\"height:200px; width:315px\" /><img alt=\"\" src=\"https://i.ibb.co/bXPVK3k/242662826-620895869280760-2190008704734124530-n.jpg\" style=\"height:200px; width:315px\" /><img alt=\"\" src=\"https://i.ibb.co/xLjvxdb/242652186-620895709280776-8666811648993028303-n.jpg\" style=\"height:200px; width:315px\" /><img alt=\"\" src=\"https://i.ibb.co/TWxX8nx/243111849-620895605947453-8205736209569953693-n.jpg\" style=\"height:200px; width:315px\" /><img alt=\"\" src=\"https://i.ibb.co/drmpWH5/242709797-620895332614147-5635908074151013823-n.jpg\" style=\"height:200px; width:315px\" /><img alt=\"\" src=\"https://i.ibb.co/VvBWMZf/180790138-