INSERT INTO `halaman` (`id`, `judul`, `konten`, `gambar`) VALUES
(13, 'Tupoksi Kesbangpol', '<p>\\</p>\r\n\r\n<table cellspacing=\"0\" class=\"MsoTableGrid\" style=\"border-collapse:collapse; border:none; margin-left:17px; width:599px\">\r\n	<tbody>\r\n		<tr>\r\n			<td style=\"height:19px; vertical-align:top; width:599px\">\r\n			<p style=\"text-align:justify\"><span style=\"font-size:12pt\"><span style=\"font-size:11.0pt\">2.1&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Tugas, Fungsi dan Struktur Organisasi</span></span></p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td style=\"height:453px; vertical-align:top; width:599px\">\r\n			<p style=\"text-align:justify\"><span style=\"font-size:12pt\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </span></p>\r\n\r\n			<p style=\"text-align:justify\"><span style=\"font-size:12pt\"><span style=\"font-size:11.0pt\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &nbsp;Konsekuensi terbit dan diundangkannya Undang &ndash; Undang Nomor 23 Tahun 2014 tentang Pemerintahan Daerah dimana urusan Kesatuan Bangsa dan Politik masuk pada Urusan pemerintahan umum yang akan dilaksanakan oleh Pemerintah Pusat, maka Badan Kesbang Pol yang merupakan OPD atau OPD pelaksana urusan kesatuan bangsa dan politik beralih statusnya menjadi Instansi Vertikal. Kemudian dalam perkembangannya,vertikalisasi Badan Kesbang Pol dan atau Instansi yang melaksanakan urusan kesatuan bangsa dan politik didaerah ditunda pelaksanaannya sampai dengan diundangkannya peraturan pelaksanaan urusan pemerintahan umum, maka Badan Kesbang Pol Kota Lubuklinggau tetap merupakan OPD Kota Lubuklinggau dibawah Walikota , sampai dengan peraturan tersebut diatas diundangkan. Dengan mengacu pada peraturan perundang-undangan lebih tinggi yaitu Permendagri Nomor 11 Tahun 2019 tentang perangkat daerah yang melaksanakan urusan pemerintahan di bidang kesatuan bangsa dan politik dan Peraturan Daerah Nomor 08 Tahun 2019 tentang pembentukan badan kesatuan bangsa dan politik kota Lubuklinggau . Berdasarkan hal tersebut maka Badan Kesbang Pol hanya melaksanakan fungsi kesatuan bangsa dan politik, sedangkan fungsi lain (perlindungan Masyarakat ) dilaksanakan oleh OPD pelaksana fungsi ketentraman dan ketertiban masyarakat. Badan Kesatuan Bangsa dan Politik merupakan unsure pendukung tugas Gubernur dibidang kesatuan bangsa dan politik, yang berkedudukan dibawah dan Bertanggung jawab kepada Walikota melalui Sekretaris Daerah (Sekda). Badan Kesbang Pol Kota Lubuklinggau mempunyai tugas pokok melaksanakan penyusunan dan pelaksanaan kebijakan daerah dibidang kesatuan bangsa, politik dan perlindungan masyarakat. Untuk melaksanakan tugas pokok tersebut, Badan Kesbang Pol menyelenggarakan tugas pokok dan fungsi sebagai berikut :</span></span></p>\r\n\r\n			<table cellspacing=\"0\" class=\"MsoTableGrid\" style=\"border-collapse:collapse; border:none; margin-left:17px; width:599px\">\r\n				<tbody>\r\n					<tr>\r\n						<td colspan=\"3\" style=\"height:17px; vertical-align:top; width:599px\">\r\n						<p style=\"margin-right:12px; text-align:justify\"><span style=\"font-size:12pt\"><span style=\"font-size:11.0pt\">2.1.1&nbsp; TUGAS POKOK</span></span></p>\r\n						</td>\r\n					</tr>\r\n					<tr>\r\n						<td colspan=\"3\" style=\"height:126px; vertical-align:top; width:599px\">\r\n						<p style=\"margin-left:2px; text-align:justify\">&nbsp;</p>\r\n\r\n						<p style=\"margin-left:2px; text-align:justify\"><span style=\"font-size:11.0pt\"><span style=\"color:black\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Melaksanakan Otonomi Pemerintah dibidang Kesatuan Bangsa dan &nbsp;Politik, seperti pembinaan kerukunan </span></span><span style=\"font-size:11.0pt\"><span style=\"color:black\">antar </span></span><span style=\"font-size:11.0pt\"><span style=\"color:black\">umat beragama, </span></span><span style=\"font-size:11.0pt\"><span style=\"color:black\">H</span></span><span style=\"font-size:11.0pt\"><span style=\"color:black\">ubungan antar Lembaga, Swadaya masyarakat, suku bangsa, ketahanan bangsa, </span></span><span style=\"font-size:11.0pt\"><span style=\"color:black\">W</span></span><span style=\"font-size:11.0pt\"><span style=\"color:black\">awasan </span></span><span style=\"font-size:11.0pt\"><span style=\"color:black\">K</span></span><span style=\"font-size:11.0pt\"><span style=\"color:black\">ebangsaan</span></span> <span style=\"font-size:11.0pt\"><span style=\"color:black\">dalam lingkungan kota lubuklinggau</span></span></p>\r\n\r\n						<p style=\"text-align:justify\">&nbsp;</p>\r\n						</td>\r\n					</tr>\r\n					<tr>\r\n						<td colspan=\"3\" style=\"height:25px; vertical-align:top; width:599px\">\r\n						<p style=\"text-align:justify\"><span style=\"font-size:12pt\"><span style=\"font-size:11.0pt\">2.1.2</span>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; <span style=\"font-size:11.0pt\">FUNGSI</span></span></p>\r\n						</td>\r\n					</tr>\r\n					<tr>\r\n						<td style=\"height:20px; vertical-align:top; width:28px\">\r\n						<p style=\"text-align:justify\">&nbsp;</p>\r\n						</td>\r\n						<td style=\"height:20px; vertical-align:top; width:28px\">\r\n						<p style=\"text-align:justify\"><span style=\"font-size:12pt\"><span style=\"font-size:11.0pt\">1</span></span></p>\r\n						</td>\r\n						<td style=\"height:20px; vertical-align:top; width:542px\">\r\n						<p style=\"text-align:justify\"><span style=\"font-size:12pt\">Perumusan kebijakan teknis bidang kesatuan bangsa dan politik;</span></p>\r\n						</td>\r\n					</tr>\r\n					<tr>\r\n						<td style=\"height:20px; vertical-align:top; width:28px\">\r\n						<p style=\"text-align:justify\">&nbsp;</p>\r\n						</td>\r\n						<td style=\"height:20px; vertical-align:top; width:28px\">\r\n						<p style=\"text-align:justify\"><span style=\"font-size:12pt\"><span style=\"font-size:11.0pt\">2</span></span></p>\r\n						</td>\r\n						<td style=\"height:20px; vertical-align:top; width:542px\">\r\n						<p style=\"text-align:justify\"><span style=\"font-size:12pt\">Penyelenggaraan urusan pemerintahan dan pelayanan umum bidang Kesatuan bangsa dan politik;</span></p>\r\n						</td>\r\n					</tr>\r\n					<tr>\r\n						<td style=\"height:20px; vertical-align:top; width:28px\">\r\n						<p style=\"text-align:justify\">&nbsp;</p>\r\n						</td>\r\n						<td style=\"height:20px; vertical-align:top; width:28px\">\r\n						<p style=\"text-align:justify\"><span style=\"font-size:12pt\"><span style=\"font-size:11.0pt\">3</span></span></p>\r\n						</td>\r\n						<td style=\"height:20px; vertical-align:top; width:542px\">\r\n						<p style=\"text-align:justify\"><span style=\"font-size:12pt\">Pembinaan, fasilitasi dan pelaksanaan tugas dibidang ideology dan kewaspadaan, ketahanan bangsa dan politik dalam negeri lingkup provinsi dan kabupaten / kota;</span></p>\r\n						</td>\r\n					</tr>\r\n					<tr>\r\n						<td style=\"height:20px; vertical-align:top; width:28px\">\r\n						<p style=\"text-align:justify\">&nbsp;</p>\r\n						</td>\r\n						<td style=\"height:20px; vertical-align:top; width:28px\">\r\n						<p style=\"text-align:justify\"><span style=\"font-size:12pt\"><span style=\"font-size:11.0pt\">4</span></span></p>\r\n						</td>\r\n						<td style=\"height:20px; vertical-align:top; width:542px\">\r\n						<p style=\"text-align:justify\"><span style=\"font-size:12pt\">Pemantauan , evaluasi dan pelaporan bidang kesatuan bangsa dan politik;</span></p>\r\n						</td>\r\n					</tr>\r\n					<tr>\r\n						<td style=\"height:20px; vertical-align:top; width:28px\">\r\n						<p style=\"text-align:justify\">&nbsp;</p>\r\n						</td>\r\n						<td style=\"height:20px; vertical-align:top; width:28px\">\r\n						<p style=\"text-align:justify\"><span style=\"font-size:12pt\"><span style=\"font-size:11.0pt\">5</span></span></p>\r\n						</td>\r\n						<td style=\"height:20px; vertical-align:top; width:542px\">\r\n						<p style=\"text-align:justify\"><span style=\"font-size:12pt\">Pelaksanaan kesekretariatan badan;</span></p>\r\n						</td>\r\n					</tr>\